  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("p", null, "\uC6F91\uC758 \uC2DC\uB300\uC5D0 \uC6F9\uC0AC\uC774\uD2B8\uAC00 \uC788\uC5C8\uB2E4\uBA74 \uC6F93\uC5D0\uB294 \uD1A0\uD070\uC774 \uC788\uB2E4. 1991\uB144 \uC6D4\uB4DC\uC640\uC774\uB4DC\uC6F9(WWW)\uC774 \uCD9C\uD604\uD558\uBA74\uC11C \uD300 \uBC84\uB108\uC2A4-\uB9AC\uB294 \uC0C8\uB85C\uC6B4 \uD45C\uC900\uC744 \uC18C\uAC1C\uD588\uB2E4. \uC774 \uD45C\uC900\uC744 \uC0AC\uC6A9\uD574\uC11C \uC6B0\uB9AC\uB294 \uCEE4\uB9E8\uB4DC\uB77C\uC778 \uC778\uD130\uD398\uC774\uC2A4\uB97C \uC0AC\uC6A9\uD558\uB294 \uB300\uC2E0\uC5D0 \uB2E8 \uBA87 \uC904\uC758 \uCF54\uB4DC\uB85C \uB208\uAE38\uC744 \uB044\uB294 \uC6F9\uD398\uC774\uC9C0\uB97C \uB9CC\uB4E4 \uC218 \uC788\uAC8C \uB418\uC5C8\uACE0 \uB9C1\uD06C\uB97C \uB530\uB77C\uAC00\uBA70 \uC778\uD130\uB137 \uC11C\uD551\uC744 \uD560 \uC218 \uC788\uAC8C \uB418\uC5C8\uB2E4. 1990\uB144\uB300 \uCD08\uBC18 \uB300\uBD80\uBD84\uC758 \uC0AC\uB78C\uB4E4\uC740 HTML \uCF54\uB4DC\uB97C \uC791\uC131\uD558\uB294 \uBC29\uBC95\uC774\uB098 \uC544\uB984\uB2F5\uACE0 \uC720\uC775\uD558\uACE0 \uC0AC\uC6A9\uD558\uAE30 \uC26C\uC6B4 \uC6F9\uC0AC\uC774\uD2B8\uB97C \uB9CC\uB4DC\uB294 \uBC29\uBC95\uC744 \uC54C\uC9C0 \uBABB\uD588\uB2E4. \uAC70\uC758 10\uB144\uC774 \uC9C0\uB098\uC11C\uC57C \uB2E8\uC9C0 \uC628\uB77C\uC778 \uC8FC\uC18C\uB85D\uC774\uB098 \uAD11\uACE0\uD310\uC744 \uB118\uC5B4\uC11C\uB294 \uC6F9\uD398\uC774\uC9C0\uB97C \uB9CC\uB4DC\uB294 \uBC29\uBC95\uC744 \uC774\uD574\uD558\uAC8C \uB418\uC5C8\uB2E4. \uADF8\uB9AC\uACE0 \uC774 \uB54C \uC6F92\uAC00 \uCD9C\uD604\uD588\uB2E4. \uC6F9\uC758 \uCD08\uCC3D\uAE30\uC640 \uBE44\uAD50\uD558\uBA74, \uC6B0\uB9AC\uB294 \uC554\uD638 \uD1A0\uD070\uC758 \uAC00\uB2A5\uC131\uC5D0 \uB300\uD55C \uC774\uD574\uC758 \uCE21\uBA74\uC5D0\uC11C \uB9E4\uC6B0 \uBE44\uC2B7\uD55C \uB2E8\uACC4\uC5D0 \uC788\uB2E4. \uBA87 \uC904\uC758 \uCF54\uB4DC\uB9CC\uC73C\uB85C \uC0C8\uB85C\uC6B4 \uD1A0\uD070\uC744 \uB9CC\uB4DC\uB294 \uAC83\uC774 \uC26C\uC6CC\uC9C0\uAE30\uB294 \uD588\uC9C0\uB9CC \uC774\uB7F0 \uD1A0\uD070\uC744 \uC5B4\uB5BB\uAC8C \uC801\uC6A9\uD560\uC9C0\uC5D0 \uB300\uD55C \uC774\uD574\uB294 \uC544\uC9C1 \uBA85\uD655\uD558\uC9C0 \uC54A\uB2E4. \uC774 \uCC45\uC744 \uC791\uC131\uD558\uB294 \uC2DC\uC810\uC5D0 \uCF54\uC778\uB9C8\uCF13\uCEA1 \uC6F9\uC0AC\uC774\uD2B8\uC5D0 \uC62C\uB77C\uC628 5400\uAC1C\uAC00 \uB118\uB294 \uD1A0\uD070\uB4E4\uC740 \uACF5\uAC1C \uAC70\uB798\uB418\uACE0 \uC788\uC9C0\uB9CC \uB300\uBD80\uBD84 \uC81C\uB300\uB85C \uB514\uC790\uC778\uB418\uC5B4\uC788\uC9C0 \uC54A\uB2E4. \uC774 \uB54C\uBB38\uC5D0 \uC0C1\uB2F9\uC218\uB294 \uACE7 \uC2E4\uD328\uD560 \uC218\uB3C4 \uC788\uB2E4."), mdx("p", null, "\uAE30\uC220\uC740 \uC544\uC9C1 \uCD08\uAE30 \uB2E8\uACC4\uC5D0 \uC788\uC9C0\uB9CC \uBE60\uB974\uAC8C \uBC1C\uC804\uD558\uACE0 \uC788\uB2E4. \uB9CE\uC740 \uC0B0\uC5C5, \uD2B9\uD788 \uC815\uBD80\uC640 \uAC70\uBC84\uB10C\uC2A4\uB97C \uD3EC\uD568\uD55C \uD654\uD3D0\uC640 \uAE08\uC735 \uBD84\uC57C\uB97C \uB4A4\uD754\uB4E4 \uAC00\uB2A5\uC131\uC744 \uAC00\uC9C0\uACE0 \uC788\uB2E4. \uD558\uC9C0\uB9CC \uD604\uC7AC\uB294 \uCD5C\uC801\uC758 \uC5C5\uBB34\uADDC\uBC94\uC774 \uC874\uC7AC\uD558\uC9C0 \uC54A\uACE0 \uB3D9\uC2DC\uC5D0 \uC218\uB9CE\uC740 \uAE30\uC220\uC801, \uBC95\uC801 \uACFC\uC81C\uB97C \uC0C1\uB300\uD574\uC57C\uD55C\uB2E4. \uB610\uD55C \uC0AC\uD68C\uACBD\uC81C\uC801\uC778 \uC601\uD5A5\uC744 \uD3EC\uD568\uD55C \uBA54\uCEE4\uB2C8\uC998, \uAC00\uB2A5\uC131, \uC704\uD611, \uAE30\uC220\uC758 \uD604\uC7AC \uC0C1\uD669 \uB4F1\uC5D0 \uB300\uD55C \uCDA9\uBD84\uD55C \uAD50\uC721\uC774 \uC774\uB8E8\uC5B4\uC838\uC788\uC9C0 \uC54A\uB2E4."), mdx("p", null, "\u201C\uC2A4\uB9C8\uD2B8 \uCEE8\uD2B8\uB809\uD2B8\u201D, \u201C\uC554\uD638\uD654\uD3D0\u201D, \u201C\uD1A0\uD070\u201D\uACFC \uAC19\uC740 \uC720\uD589\uC5B4\uB4E4\uC740 \uD63C\uB780\uC744 \uBD80\uCD94\uAE30\uACE0 \uC788\uB2E4. \uD3B8\uD30C\uC801\uC774\uACE0 \uBD80\uBD84\uC801\uC778\x1D \uC778\uC2DD\uC774 \uB298\uC5B4\uB098\uACE0 \uC788\uACE0, \uC6F93 \uB124\uD2B8\uC6CC\uD06C\uAC00 \uC65C \uADF8\uB9AC\uACE0 \uC5B4\uB5BB\uAC8C \uBBF8\uB798\uC758 \uAC00\uC7A5 \uD06C\uACE0 \uD68D\uAE30\uC801\uC778 \uBCC0\uD654\uB97C \uBD88\uB7EC\uC62C \uC218 \uC788\uC744\uC9C0\uC5D0 \uB300\uD55C \uD070 \uADF8\uB9BC\uC740 \uC5EC\uC804\uD788 \uBD88\uD655\uC2E4\uD558\uB2E4. \uC5B8\uB860\uC740 \uACC4\uC18D \uC554\uD638\uD654\uD3D0\uB97C \uC5B8\uAE09\uD558\uC9C0\uB9CC \uC2E4\uC0C1\uC740 \uBE44\uD654\uD3D0 \uD1A0\uD070\uC744 \uB9D0\uD560 \uB54C\uB3C4 \uB9CE\uB2E4. \uB610, \uAE30\uBC18\uC774 \uB418\uB294 \uBE14\uB85D\uCCB4\uC778 \uB124\uD2B8\uC6CC\uD06C\uB97C \uD22C\uAE30\uC758 \uB300\uC0C1\uC73C\uB85C \uCD95\uC18C\uC2DC\uD0A4\uAE30\uB3C4 \uD55C\uB2E4. \uD558\uC9C0\uB9CC \uBE14\uB85D\uCCB4\uC778\uC740 \uBB34\uC5C7\uBCF4\uB2E4\uB3C4 \uBA3C\uC800 \uAC70\uBC84\uB10C\uC2A4\uC758 \uC778\uD504\uB77C\uB97C \uC81C\uACF5\uD558\uBA70, \uC624\uB298\uB0A0 \uC778\uD130\uB137\uC758 \uB9CE\uC740 \uBB38\uC81C\uB97C \uD574\uACB0\uD560 \uC218 \uC788\uB2E4\uB294 \uC0AC\uC2E4\uC5D0\uB294 \uAD00\uC2EC\uC744 \uC8FC\uC9C0 \uC54A\uB294\uB2E4. \uD604\uC7AC\uC758 \uC778\uD130\uB137\uC5D0\uB294 \uAC1C\uC778\uC815\uBCF4\uB97C \uCC98\uB9AC\uD558\uB294 \uBC29\uBC95\uC5D0 \uB300\uD574 \uC0AC\uC6A9\uC790\uC758 \uAD8C\uD55C\uC774 \uC5C6\uACE0, \uBB3C\uAC74, \uC11C\uBE44\uC2A4, \uAE08\uC735\uACB0\uC81C\uC758 \uACF5\uAE09\uB9DD\uC5D0\uB294 \uD22C\uBA85\uC131\uC774 \uC5C6\uC73C\uBA70, \uC778\uD130\uB137 \uACE0\uC720\uC758 \uC9C0\uBD88\uACB0\uC81C \uB808\uC774\uC5B4\uAC00 \uC874\uC7AC\uD558\uC9C0 \uC54A\uB294\uB2E4. \uB530\uB77C\uC11C \uC544\uB9C8\uC874, \uC5D0\uC5B4\uBE44\uC564\uBE44, \uC6B0\uBC84\uC640 \uAC19\uC740 \uC778\uD130\uB137 \uD50C\uB7AB\uD3FC\uC744 \uBBFF\uACE0 \uC758\uC874\uD574\uC57C \uD558\uB294 \uC0C1\uD669\uC774\uB2E4."), mdx("p", null, "\uC5C5\uACC4\uB294 \u201C\uBE44\uD2B8\uCF54\uC778\u201D\uACFC \u201C\uBE14\uB85D\uCCB4\uC778\u201D\uC744 \uB2E4\uB978 \uAC83\uC73C\uB85C \uCDE8\uAE09\uD574\uC11C \uC885\uC885 \uC778\uC704\uC801\uC778 \uBD84\uC5F4\uC744 \uB9CC\uB4E4\uACE0 \uC624\uD574\uB97C \uAC00\uC838\uC628\uB2E4. \uBE44\uD2B8\uCF54\uC778\uB3C4 \uBE14\uB85D\uCCB4\uC778 \uB124\uD2B8\uC6CC\uD06C\uB77C\uB294 \uC0AC\uC2E4\uC744 \uB9CE\uC774\uB4E4 \uBAA8\uB974\uB294 \uAC83\uCC98\uB7FC \uBCF4\uC778\uB2E4. 1) \uC804\uC138\uACC4\uC801\uC73C\uB85C \uC54C\uC9C0 \uBABB\uD558\uB294 \uC0AC\uB78C\uB4E4\uC774 \uAD00\uB9AC\uD558\uACE0, 2) \uCEE8\uC13C\uC11C\uC2A4 \uD504\uB85C\uD1A0\uCF5C\uB85C \uC791\uB3D9\uD558\uBA70, 3) \uACE0\uC720\uD1A0\uD070\uC73C\uB85C \uB124\uD2B8\uC6CC\uD06C\uC5D0 \uAE30\uC5EC\uD55C \uBAA8\uB4E0 \uCC38\uC5EC\uC790\uB4E4\uC5D0\uAC8C \uBCF4\uC0C1\uC744 \uC8FC\uB294 \uAC83\uC774 \uBE14\uB85D\uCCB4\uC778 \uB124\uD2B8\uC6CC\uD06C\uC774\uB2E4. \uAC70\uBC84\uB10C\uC2A4 \uADDC\uC57D\uC740 \uBE14\uB85D\uCCB4\uC778\uC758 \uACE0\uC720\uD1A0\uD070\uC758 \uC0DD\uC131\uACFC \uC5F0\uAD00\uB418\uC5B4 \uC788\uB2E4. \uB530\uB77C\uC11C \uBE44\uD2B8\uCF54\uC778 \uD1A0\uD070\uC740 \uBE44\uD2B8\uCF54\uC778 \uB124\uD2B8\uC6CC\uD06C\uB77C\uACE0 \uD558\uB294 \uBD84\uC0B0\uD654 \uC778\uD130\uB137 \uBD80\uC871\uC758 \uD654\uD3D0\uB85C \uBCFC \uC218 \uC788\uB2E4. \uC5EC\uAE30\uC11C \uB124\uD2B8\uC6CC\uD06C \uCC38\uC5EC\uC790\uB294 \uBE44\uD2B8\uCF54\uC778\uC73C\uB85C \uBCF4\uC0C1\uC744 \uBC1B\uB294\uB2E4. \uB9C8\uCE58 \uBD84\uC0B0\uD654 \uC778\uD130\uB137\uC758 \uBD80\uC871\uC778 \uC774\uB354\uB9AC\uC6C0 \uB124\uD2B8\uC6CC\uD06C\uC758 \uD654\uD3D0\uAC00 \uC774\uB354(Ether)\uC774\uACE0, \uC2DC\uC544 \uB124\uD2B8\uC6CC\uD06C\uC758 \uACE0\uC720\uD654\uD3D0\uAC00 \uC2DC\uC544(Sia)\uC778 \uAC83\uACFC \uB9C8\uCC2C\uAC00\uC9C0\uC774\uB2E4. \uBE44\uD2B8\uCF54\uC778 \uB124\uD2B8\uC6CC\uD06C\uC640 \uAE30\uD0C0 \uBD84\uC0B0\uC6D0\uC7A5\uC740 \uBAA8\uB450 \uACF5\uB3D9\uC73C\uB85C \uC720\uC9C0\uD558\uB294 \uACF5\uACF5 \uC778\uD504\uB77C\uB97C \uB9D0\uD558\uBA70 \uCC28\uC138\uB300 \uC778\uD130\uB137\uC758 \uCC99\uCD94\uAC00 \uB41C\uB2E4. \uC774\uB97C \uD06C\uB9BD\uD1A0 \uCEE4\uBBA4\uB2C8\uD2F0\uC5D0\uC11C\uB294 \uC6F93\uB77C\uACE0 \uBD80\uB978\uB2E4."), mdx("p", null, "\uCD08\uAE30\uC758 \uD1A0\uD070\uB4E4\uC740 \uAE30\uBC18\uC774 \uB418\uB294 \uBE14\uB85D\uCCB4\uC778 \uD504\uB85C\uD1A0\uCF5C\uC758 \uBCF4\uC0C1\uACC4\uD68D\uC758 \uC77C\uBD80\uB85C\uC11C\uB9CC \uB9CC\uB4E4\uC5B4\uC84C\uB358 \uBC18\uBA74, \uC774\uB354\uB9AC\uC6C0 \uB124\uD2B8\uC6CC\uD06C\uC758 \uB4F1\uC7A5\uC73C\uB85C \uD1A0\uD070\uC740 \uAE30\uC220\uC801\uC73C\uB85C \uC5C5\uADF8\uB808\uC774\uB4DC\uB418\uC5C8\uB2E4. \uC774\uB354\uB9AC\uC6C0\uC740 \uB2E8\uC21C\uD55C \uC2A4\uB9C8\uD2B8 \uCEE8\uD2B8\uB809\uD2B8\uB97C \uC0AC\uC6A9\uD574\uC11C \uCF54\uB4DC \uBA87 \uC904\uB9CC\uC73C\uB85C \uD1A0\uD070\uC758 \uBC1C\uD589\uC774 \uAC00\uB2A5\uD558\uB3C4\uB85D \uC27D\uACE0 \uC800\uB834\uD558\uAC8C \uB9CC\uB4E4\uC5C8\uACE0, \uAC01\uC790 \uB530\uB85C \uBE14\uB85D\uCCB4\uC778 \uC778\uD504\uB77C\uB97C \uAD6C\uCD95\uD560 \uD544\uC694\uAC00 \uC5C6\uAC8C \uB418\uC5C8\uB2E4. \uD558\uC9C0\uB9CC \uC544\uC9C1 \uC0AC\uB78C\uB4E4\uC740 \uC774 \uD1A0\uD070\uC744 \uAC00\uC9C0\uACE0 \uBB34\uC5BC \uD560 \uC218 \uC788\uC744\uC9C0, \uC5B4\uB5BB\uAC8C \uC801\uC808\uD788 \uC124\uACC4\uD574\uC57C\uD560\uC9C0 \uBAA8\uB974\uB294 \uC0C1\uD669\uC774\uB2E4. \uAE30\uD0C0 \uACFC\uC81C\uB85C\uB294 \uAE30\uC220\uC801\uC778 \uBB38\uC81C, \uBAA9\uC801\uC8FC\uB3C4 \uD1A0\uD070\uC758 \uC9C0\uC18D\uAC00\uB2A5\uD55C \uBA54\uCEE4\uB2C8\uC998 \uC124\uACC4, \uBD88\uBA85\uD655\uD558\uACE0 \uC81C\uAC01\uAC01\uC778 \uAD00\uB828 \uBC95\uC548 \uADF8\uB9AC\uACE0 \uB5A0\uC624\uB974\uB294 \u201C\uD1A0\uD070 \uACBD\uC81C\u201D\uC758 \uAC00\uB2A5\uC131\uACFC \uC704\uD611\uC5D0 \uB300\uD55C \uAD50\uC721\uC758 \uBD80\uC7AC \uB4F1\uC774 \uC788\uB2E4."), mdx("p", null, "\uB530\uB77C\uC11C \uC774 \uCC45\uC758 \uBAA9\uD45C\uB294 \uCCAB\uC9F8\uB85C \uC6F93\uC758 \uADFC\uBCF8\uC801\uC778 \uC124\uACC4 \uB2E8\uC704(\uD30C\uD2B8 1)\uC5D0 \uB300\uD55C \uAC1C\uC694\uB97C \uC81C\uACF5\uD558\uACE0 \uC2A4\uB9C8\uD2B8 \uCEE8\uD2B8\uB809\uD2B8, \uB2E4\uC624(DAO), \uD1A0\uD070\uACFC \uAC19\uC740 \uAC00\uC7A5 \uC911\uC694\uD55C \uC6F9 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uC0AC\uD68C\uACBD\uC81C\uC801\uC7